package Practice;

public class CountDown {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i = 0; i<=10; i++) {
			System.out.println("count" + i);
			
		}
		
		for(int j = 10; j>= 0; j = j--) {
			System.out.println("count" + j);
		}
	}

}
